#include <stdlib.h>
#include <unistd.h>


void brainfuck(char **str, char **arr);

void v_forward(char **str, char **arr) {
    
    int count = 1;
    if (!(**arr)) {   
        while (count && *str) {
            (*str)++;
            if (**str == '[')
                ++count; 
            if (**str == ']')
                --count;
        }
    }
}

void v_back(char **str, char **arr) {
    
    int count = 1;
    if (**arr) {
        while (count && *str) {
            (*str)--;
            if (**str == '[')
                --count;
            if (**str == ']')
                ++count;        
        }
    }
}

void brainfuck(char **str, char **arr) {

    while (**str) {
        if (**str == '>')
            (*arr)++;
        else if (**str == '<')
            (*arr)--;
        else if (**str == '+')
            (**arr)++;
        else if (**str == '-')
            (**arr)--;
        else if (**str == '.')
            write(1, *arr, 1);
        else if (**str == '[')
            v_forward(str, arr);
        else if (**str == ']')
            v_back(str, arr);
        (*str)++;
    }
}


